#import "MKPinAnnotationView.h"

@implementation MKPinAnnotationView

@synthesize pinColor;
@synthesize animatesDrop;

- (id)initWithAnnotation:(id <MKAnnotation>)anAnnotation reuseIdentifier:(NSString *)aReuseIdentifier
{
    if (self = [super initWithAnnotation:anAnnotation reuseIdentifier:aReuseIdentifier]) {
        self.canShowCallout = YES;
    }
    return self;
}

- (NSString *)imageUrl
{
    CGFloat scale = [[UIScreen mainScreen] scale];
    NSString *dataURI = nil;
    switch (pinColor) {
        case MKPinAnnotationColorRed:
            if (scale >= 2.0f) {
                // MKPinAnnotationColorRed@2x.png
                dataURI = @"data:image/png;base64,";
            } else {
                // MKPinAnnotationColorRed.png
                dataURI = @"data:image/png;base64,";
            }
            break;
        case MKPinAnnotationColorGreen:
            if (scale >= 2.0f) {
                // MKPinAnnotationColorGreen@2x.png
                dataURI = @"data:image/png;base64,";
            } else {
                // MKPinAnnotationColorGreen.png
                dataURI = @"data:image/png;base64,";
            }
            break;
        case MKPinAnnotationColorPurple:
            if (scale >= 2.0f) {
                // MKPinAnnotationColorPurple@2x.png
                dataURI = @"data:image/png;base64,";
            } else {
                // MKPinAnnotationColorPurple.png
                dataURI = @"data:image/png;base64,";
            }
            break;
        default:
            if (scale >= 2.0f) {
                // MKPinAnnotationColorRed@2x.png
                dataURI = @"data:image/png;base64,";
            } else {
                // MKPinAnnotationColorRed.png
                dataURI = @"data:image/png;base64,";
            }
            break;
    }
    return dataURI;
}

- (CGSize)imageSize
{
    return CGSizeMake(47.0f, 37.0f);
}

@end
